package com.fabasoad.leetcode.medium;

import java.util.HashSet;
import java.util.Set;

public class Solution_1525 {

  public int numSplits(String s) {
    int[] left = new int[s.length() - 1];
    int[] right = new int[s.length() - 1];
    final Set<Character> setL = new HashSet<>();
    final Set<Character> setR = new HashSet<>();
    int to = s.length() - 1;
    for (int i = 0; i < to; i++) {
      setL.add(s.charAt(i));
      left[i] = setL.size();
      setR.add(s.charAt(to - i));
      right[to - i - 1] = setR.size();
    }
    int res = 0;
    for (int i = 0; i < s.length() - 1; i++) {
      if (left[i] == right[i]) {
        res++;
      }
    }
    return res;
  }

  public static void main(String[] args) {
    final Solution_1525 s = new Solution_1525();
    assert 2 == s.numSplits("aacaba");
    assert 1 == s.numSplits("abcd");
    assert 9744 == s.numSplits("");
  }
}
