package com.fabasoad.leetcode.medium;

import java.util.HashMap;
import java.util.HashSet;
import java.util.Map;
import java.util.Set;

public class Solution_1525 {

  private int calculateDistinct(Map<String, Integer> map, String str) {
    map.computeIfAbsent(str, (k) -> {
      final Set<Character> set = new HashSet<>();
      for (int j = 0; j < k.length(); j++) {
        set.add(k.charAt(j));
      }
      return set.size();
    });
    return map.get(str);
  }

  public int numSplits(String s) {
    final Map<String, Integer> map = new HashMap<>();
    int res = 0;
    for (int i = 1; i < s.length(); i++) {
      String left = s.substring(0, i);
      String right = s.substring(i);
      if (calculateDistinct(map, left) == calculateDistinct(map, right)) {
        res++;
      }
    }
    return res;
  }

  public static void main(String[] args) {
    final Solution_1525 s = new Solution_1525();
    assert 2 == s.numSplits("aacaba");
    assert 1 == s.numSplits("abcd");
    assert 9744 == s.numSplits("");
  }
}
